#!/usr/bin/env python3

from functools import reduce


NUM = ''

def iterate_substring(text, count):
    total_length = len(text)
    return [text[x:x+count:] for x in range(0, total_length - count + 1)]

def sort_string(text):
    x = list(text)
    x.sort()
    return ''.join(x)

def string_product(text):
    x = list(text)
    x_int = list(map(int, x))
    return reduce(lambda x, y: x * y, x_int)

def main():
    COUNT = 13

    all_substrings = iterate_substring(NUM, COUNT)
    max_index = 0
    max_sub = sort_string(all_substrings[0])

    for index, sub in enumerate(all_substrings):
        if '0' in sub:
            continue

        sorted_sub = sort_string(sub)

        if sorted_sub > max_sub:
            max_index = index
            max_sub = sorted_sub

    print('index:', max_index)
    print('max substring:', max_sub)
    print('original max substring:', NUM[max_index:max_index+COUNT])
    print('product:', string_product(max_sub))

if __name__ == '__main__':
    main()
